var sidemenu = document.getElementById("sidemenu");
function showMenu() {
    sidemenu.style.right = "0";
}
function hideMenu() {
    sidemenu.style.right = "-200px";
}

// Tabs
var tablinks = document.getElementsByClassName("tab-links");
var tabcontents = document.getElementsByClassName("tab-contents");

function opentab(tabname) {
    for (tablink of tablinks) {
        tablink.classList.remove("active-link");
    }
    for (tabcontent of tabcontents) {
        tabcontent.classList.remove("active-tab");
    }
    event.currentTarget.classList.add("active-link");
    document.getElementById(tabname).classList.add("active-tab");
}
function showMoreProjects() {
    // Show hidden projects
    document.querySelector('.more-projects').style.display = 'grid';
    // Hide the 'See More Projects' button
    document.querySelector('.button-btn').style.display = 'none';
}
// Google Sheets Form Submission
const scriptURL = 'https://script.google.com/macros/s/AKfycbyMcyldk8TVPm4Ssv89ovH4PCC4vboBfKG95M5gmTNRlw3JRUwFSQuqZnU_O3g0AJI4iA/exec';
const form = document.forms['submit-to-google-sheet'];
const msg = document.getElementById("msg");

form.addEventListener('submit', e => {
    e.preventDefault();
    fetch(scriptURL, { method: 'POST', body: new FormData(form)})
        .then(response => {
            msg.innerHTML = "Message sent successfully!";
            setTimeout(() => {msg.innerHTML = "";}, 5000);
            form.reset();
        })
        .catch(error => console.error('Error!', error.message));
});